obtenerElementos.sql:
SET NOCOUNT ON; DECLARE @inicio INT; SET @inicio= (@pagina-1)*@longitudPagina;--Pagina debe ser mayor que 0; DECLARE @TablaAuxiliarPaginacion AS TABLE( indiceRegistro INT, cve_subcatalogo INT, descripcion VARCHAR(50), valor1 VARCHAR(100), valor2 VARCHAR(100), activo bit )
INSERT INTO @TablaAuxiliarPaginacion SELECT ROW_NUMBER() OVER(ORDER BY CONVERT(INT,cve_catalogo ) DESC) AS indiceRegistro, cve_subcatalogo, descripcion, valor1, valor2, activo FROM ES_catperdet WHERE ( ((CONVERT(VARCHAR(20),cve_subcatalogo) like '%' + @criterio + '%') OR (descripcion like '%' + @criterio + '%'))) AND cve_catalogo=@cve_catalogo;
--Paginacion SELECT TOP(@longitudPagina) indiceRegistro, @@ROWCOUNT as totalRegistros, a.cve_subcatalogo, a.descripcion, a.valor1, a.valor2, a.activo FROM @TablaAuxiliarPaginacion a WHERE indiceRegistro > @inicio ORDER BY indiceRegistro ASC

Nuevo.sql:

SET NOCOUNT ON;
BEGIN TRY
  INSERT INTO ES_catperdet(cve_catalogo,cve_subcatalogo,descripcion,valor1,valor2,activo)
  VALUES(@Catalogoclave,@clave,@descripcion,@Valor1,@Valor2,@activo)
  SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
END TRY BEGIN CATCH
  SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH
