Nuevo.sql:
  BEGIN
  SET NOCOUNT ON;
BEGIN TRY
  INSERT INTO ES_catpermae(cve_catalogo,descripcion,label1,label2,activo)
  VALUES(@clave,@descripcion,@Etiqueta1,@Etiqueta2,@activo)
  SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
END TRY BEGIN CATCH
  SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH
END

obtenerCatalogos.sql:
SET NOCOUNT ON; DECLARE @inicio INT; SET @inicio= (@pagina-1)*@longitudPagina;--Pagina debe ser mayor que 0; DECLARE @TablaAuxiliarPaginacion AS TABLE( indiceRegistro INT, cve_catalogo INT, descripcion VARCHAR(50), activo bit )
INSERT INTO @TablaAuxiliarPaginacion SELECT ROW_NUMBER() OVER(ORDER BY CONVERT(INT,cve_catalogo ) DESC) AS indiceRegistro, cve_catalogo, descripcion, activo FROM ES_catpermae WHERE ( ((CONVERT(VARCHAR(20),cve_catalogo) like '%' + @criterio + '%') OR (descripcion like '%' + @criterio + '%')) AND @cve_catalogo=0) OR ((cve_catalogo=@cve_catalogo) AND (@cve_catalogo != 0));
--Paginacion SELECT TOP(@longitudPagina) indiceRegistro, @@ROWCOUNT as totalRegistros, a.cve_catalogo, a.descripcion, a.activo FROM @TablaAuxiliarPaginacion a WHERE indiceRegistro > @inicio ORDER BY indiceRegistro ASC
