Nuevo.sql:

SET NOCOUNT ON;
BEGIN TRY
    INSERT INTO ES_catcontroles(cve_tipocontrol,descripcion)
    VALUES(@cve_tipocontrol,@descripcion)
    SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
END TRY BEGIN CATCH
    SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH
