Nuevo.sql:

SET NOCOUNT ON;
BEGIN TRY
    INSERT INTO EXA_paqexamen(cve_paqexamen,descripcion)
    VALUES(@cve_paqexamen,@descripcion)
    SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
END TRY BEGIN CATCH
    SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH

ObtenerCatalogo.sql:
SET NOCOUNT ON; DECLARE @inicio INT; SET @inicio= (@pagina-1)*@longitudPagina;--Pagina debe ser mayor que 0; DECLARE @TablaAuxiliarPaginacion AS TABLE( indiceRegistro INT, [cve_paqexamen] [int], [descripcion] [nvarchar](255) )
INSERT INTO @TablaAuxiliarPaginacion SELECT ROW_NUMBER() OVER(ORDER BY CONVERT(INT,cve_paqexamen ) DESC) AS indiceRegistro, cve_paqexamen,descripcion FROM EXA_paqexamen WHERE ( ((CONVERT(VARCHAR(20),cve_paqexamen) like '%' + @criterio + '%') OR (descripcion like '%' + @criterio + '%')) AND @cve_paqexamen=0) OR ((cve_paqexamen=@cve_paqexamen) AND (@cve_paqexamen != 0));
--Paginacion SELECT TOP(@longitudPagina) indiceRegistro, @@ROWCOUNT as totalRegistros, a.cve_paqexamen,a.descripcion FROM @TablaAuxiliarPaginacion a WHERE indiceRegistro > @inicio ORDER BY indiceRegistro ASC

AgregarExamenAlPaquete.sql:

SET NOCOUNT ON;
BEGIN TRY
  INSERT INTO EXA_paqexamendet VALUES(@cve_paqexamen,@cve_examen)
  SELECT 1  estatus, 'Registro asignado correctamente' mensaje;
END TRY BEGIN CATCH
  SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH

EliminarExamenDelPaquete.sql:

SET NOCOUNT ON;
BEGIN TRY
  DELETE FROM EXA_paqexamendet WHERE cve_paqexamen=@cve_paqexamen AND cve_examen=@cve_examen;
  SELECT 1  estatus, 'Registro eliminado correctamente.' mensaje;
END TRY BEGIN CATCH
  SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH
