Nuevo.sql:

BEGIN
SET NOCOUNT ON;
BEGIN TRY
INSERT INTO ES_pstperfilsubseccion(cve_seccion,cve_subseccion,descripcion,secuencia,activo,multivalores,cve_tipocontrol,cve_catalogo,cve_tipocatalogo,valor)
VALUES(@cve_seccion,@cve_subseccion,@descripcion,@secuencia,@activo,@multivalores,@cve_tipocontrol,@cve_catalogo,@tipocatalogo,@valor)
SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
END TRY BEGIN CATCH
SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH
END

Editar.sql:
BEGIN SET NOCOUNT ON; BEGIN TRY
UPDATE ES_pstperfilsubseccion SET descripcion=@descripcion, secuencia=@secuencia, activo=@activo, multivalores=@multivalores, cve_tipocontrol=@cve_tipocontrol, cve_catalogo=@cve_catalogo, cve_tipocatalogo=@tipocatalogo, valor=@valor WHERE cve_seccion=@cve_seccion AND cve_subseccion=@cve_subseccion SELECT 1 estatus, 'Registro guardado correctamente' mensaje;
END TRY BEGIN CATCH SELECT -1 AS estatus, 'Error: ' + ERROR_MESSAGE() mensaje; END CATCH END

obtenerApartados.sql:
SET NOCOUNT ON; DECLARE @inicio INT; SET @inicio= (@pagina-1)*@longitudPagina;--Pagina debe ser mayor que 0; DECLARE @TablaAuxiliarPaginacion AS TABLE( indiceRegistro INT, cve_subseccion smallint, descripcion nvarchar(80), secuencia smallint, activo bit, multivalores nvarchar(50), cve_tipocontrol smallint , cve_catalogo smallint , cve_tipocatalogo smallint)
INSERT INTO @TablaAuxiliarPaginacion SELECT ROW_NUMBER() OVER(ORDER by secuencia ) AS indiceRegistro, cve_subseccion, descripcion, secuencia, activo, multivalores, cve_tipocontrol, cve_catalogo, cve_tipocatalogo FROM ES_pstperfilsubseccion WHERE ( ((CONVERT(VARCHAR(20),cve_subseccion) like '%' + @criterio + '%') OR (descripcion like '%' + @criterio + '%'))) AND cve_seccion=@cve_seccion;
--Paginacion SELECT TOP(@longitudPagina) indiceRegistro, @@ROWCOUNT as totalRegistros, a.cve_subseccion, a.descripcion, a.secuencia, a.activo, a.multivalores, B.descripcion AS 'tipocontrol', C.descripcion AS 'catalogo', D.descripcion AS 'tipocatalogo' FROM @TablaAuxiliarPaginacion a LEFT JOIN ES_catcontroles B ON A.cve_tipocontrol=B.cve_tipocontrol LEFT JOIN ES_catpermae C ON A.cve_catalogo=C.cve_catalogo LEFT JOIN ES_tipocatalogo D ON A.cve_tipocatalogo=D.cve_tipocatalogo WHERE indiceRegistro > @inicio ORDER BY a.secuencia ASC
