ObtenerSiguienteClave.sql:

BEGIN
  SELECT ISNULL(MAX(cve_motivo),0) + 1 cve_motivo FROM CTR_motivojustifi;
END

Nuevo.sql:


              BEGIN
                  SET NOCOUNT ON;
                  SET DATEFORMAT dmy;
                  BEGIN TRY
                    INSERT INTO CTR_motivojustifi(
                        cve_tipojustificacion,
	                      cve_motivo,
	                      descripcion,
	                      activo,
	                      tolerancia,
	                      cve_dadotolerancia,
	                      permitejustifica,
                        aplicaparapolitica,
                        permitereponerdia,
	                      escaneo,
	                      congose,
	                      plazo,
	                      cve_dadoplazo,
                        cve_flujo
                    )
                    VALUES(                      
                        @cve_tipojustificacion,
	                      @cve_motivo,
	                      @descripcion,
	                      @activo,
	                      @tolerancia,
	                      @cve_dadotolerancia,
	                      @permitejustifica,
                        @aplicaparapolitica,
                        @permitereponerdia,
	                      @escaneo,
	                      @congose,
	                      @plazo,
	                      @cve_dadoplazo,
                        @cve_flujo
                    );
                    SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
                  END TRY BEGIN CATCH
                      SELECT -1 AS estatus,  'Error: No se guardo el registro. Detalle:' + ERROR_MESSAGE() mensaje;
                  END CATCH
              END
