ObtenerSiguienteClave.sql:

BEGIN
  SELECT ISNULL(MAX(indice),0) + 1 indice FROM CTR_politicas;
END

Nuevo.sql:


BEGIN
   BEGIN TRY
    SET NOCOUNT ON;
    INSERT INTO CTR_politicas(
        indice,
        cve_politica,
        cantidad,
        frecuencia,
        cve_dado,
        aux1,
        aux2,
        aux3,
        aux4,
    )
    VALUES(
        @indice,
        @cve_politica,
        @Cantidad,
        @frecuencia,
        @cve_dado,
        @aux1,
        @aux2,
        @aux3,
        @aux4
    );                  
    SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
   END TRY BEGIN CATCH                 
        SELECT -1  estatus, 'Error:' + ERROR_MESSAGE() mensaje;
   END CATCH
END
