obtenerSiguienteClave.sql:
BEGIN
    SELECT ISNULL(MAX(cve_politica),0) + 1 clave FROM VAC_PoliVacMaes;
END

Nuevo.sql:
BEGIN
    SET NOCOUNT ON;
    BEGIN TRY
        INSERT INTO VAC_PoliVacMaes(
            cve_politica,
            nombre,
            diaspermitidos,
            porcentajedeanios,
            activo
        )
        VALUES(
            @cve_politica,
            @nombre,
            @diaspermitidos,
            @porcentajedeanios,
            @activo
        )
        SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
    END TRY BEGIN CATCH
        SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
    END CATCH
END

obtenerSiguienteClave.sql:
BEGIN
    SELECT ISNULL(MAX(cve_politica),0) + 1 clave FROM VAC_PoliticaVacaciones;
END

Nuevo.sql:
BEGIN
    SET NOCOUNT ON;
    BEGIN TRY
        INSERT INTO VAC_PoliticaVacaciones(
            cve_grupopoliticas,
            cve_politica,
            anios,
            diastomar,
            primavacacional
        )
        VALUES(
            @cve_grupopoliticas,
            @cve_politica,
            @anios,
            @diastomar,
            @primavacacional
        )
        SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
    END TRY BEGIN CATCH
        SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
    END CATCH
END
