obtenerSiguienteClave.sql:
BEGIN
    SELECT ISNULL(MAX(cve_puesto),0) + 1 clave FROM ES_puestosgenericos;
END

Nuevo.sql:
BEGIN
    SET NOCOUNT ON;
    BEGIN TRY
        INSERT INTO ES_puestosgenericos(
            cve_puesto,
            puntos,
            nivelmercer,
            tabulador,
            niveltabular,
            nombre_puesto,
            cve_extra,
            cve_paqexamen
        )
        VALUES(
            @cve_puesto,
            @puntos,
            @nivelmercer,
            @tabulador,
            @niveltabular,
            @nombre_puesto,
            @cve_extra,
            @cve_paqexamen
        )
        SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
    END TRY BEGIN CATCH
        SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
    END CATCH
END

obtenerCatalogo.sql:
BEGIN SET NOCOUNT ON; DECLARE @inicio INT; SET @inicio= (@pagina-1)*@longitudPagina;--Pagina debe ser mayor que 0; DECLARE @TablaAuxiliarPaginacion AS TABLE( indiceRegistro INT, cve_puesto INT, cve_departamento INT, puntos INT, nivelmercer INT, tabulador INT, niveltabular INT, nombre_puesto VARCHAR(200), cve_extra VARCHAR(200), tienePerfil BIT, perfilescaneado BIT, cve_paqexamen INT ) INSERT INTO @TablaAuxiliarPaginacion SELECT ROW_NUMBER() OVER(ORDER BY nombre_puesto ASC) AS indiceRegistro, ES_puestosgenericos.cve_puesto, B.cve_departamento cve_departamento, puntos, nivelmercer, tabulador, niveltabular, nombre_puesto, cve_extra, CASE WHEN EXISTS(select * from Es_PstPerfilValor where num_platilla=@cve_puesto) THEN 1 ELSE 0 END AS tienePerfil, perfilescaneado, cve_paqexamen FROM ES_puestosgenericos LEFT OUTER JOIN ES_puestosdepartamento B ON ES_puestosgenericos.cve_puesto=B.cve_puesto AND B.cve_departamento=@cve_departamento
WHERE ( ((CONVERT(VARCHAR(20),ES_puestosgenericos.cve_puesto) like '%' + @criterio + '%') OR (nombre_puesto like '%' + @criterio + '%')) AND @cve_puesto=0) OR ((ES_puestosgenericos.cve_puesto=@cve_puesto) AND (@cve_puesto != 0));
--Paginacion SELECT TOP(@longitudPagina) indiceRegistro, @@ROWCOUNT as totalRegistros, a.cve_puesto, a.cve_departamento, a.puntos, a.nivelmercer, a.tabulador, a.niveltabular, a.nombre_puesto, a.cve_extra, a.tienePerfil, a.perfilescaneado, b.descripcion as nombretabulador, a.cve_paqexamen FROM @TablaAuxiliarPaginacion a left outer join ES_TabuladorDescripciones b on a.tabulador=b.cve_tabulador WHERE indiceRegistro > @inicio ORDER BY a.cve_departamento DESC,indiceRegistro ASC END

GuardarPuestosDepartamento.sql:
                BEGIN

                    DECLARE @estatus INT;SET @estatus=0;
	                DECLARE @mensaje VARCHAR(200);SET @mensaje='No se ejecuto la instrucción';

                    BEGIN TRANSACTION T1
                        DELETE FROM ES_puestosdepartamento WHERE cve_departamento=@cve_departamento;
                        INSERT INTO ES_puestosdepartamento(cve_departamento,cve_puesto, nombrepuesto) SELECT @cve_departamento,spliter,'' cve_puesto FROM split(@cves_puestos,',');
                    IF @@TRANCOUNT=1 BEGIN
				        COMMIT TRANSACTION T1
				        SET @estatus= '1';
				        SET @mensaje= 'Se agregaron correctamente los puestos al departamento.';
			        END ELSE BEGIN
				        ROLLBACK TRANSACTION T1
				        SET @estatus= '-1';
				        SET @mensaje= 'No se realizó la operación.';
			        END

		            SELECT @estatus estatus, @mensaje mensaje;                    
                END

AgregarPuestoDepartamento.sql:

BEGIN TRY
     INSERT INTO ES_puestosdepartamento(cve_departamento,cve_puesto) VALUES(@cve_departamento,@cve_puesto);
      SELECT 1  estatus, 'Puesto agregado al departamento correctamente' mensaje;
  END TRY BEGIN CATCH
      SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
  END CATCH

EliminarPuestoDepartamento.sql:

BEGIN TRY
    DELETE FROM ES_puestosdepartamento WHERE cve_departamento=@cve_departamento AND cve_puesto=@cve_puesto;
    SELECT 1  estatus, 'Puesto eliminado del departamento correctamente' mensaje;
END TRY BEGIN CATCH
    SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
END CATCH
