obtenerSiguienteClave.sql:
BEGIN
SELECT ISNULL(MAX(cve_empresa),0) + 1 clave FROM SG_empresas;
END

Nuevo.sql:
BEGIN
    SET NOCOUNT ON;
    BEGIN TRY
        INSERT INTO SG_empresas(
            cve_empresa,
            nombre,
            rfc,
            activo,
            cve_pais,
            fechaalta,
            razonsocial,
            idusuariocreo,
            cve_giro,
            calle,
            colonia,
            delegmunic,
            estado,
            cp
        )
        VALUES(
            @cve_empresa,
            @nombre,
            @rfc,
            @activo,
            @cve_pais,
            convert(datetime,@fechaalta,103),
            @razonsocial,
            @idusuariocreo,
            @cve_giro,
            @calle,
            @colonia,
            @delegmunic,
            @estado,
            @cp 
        )
        SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
    END TRY BEGIN CATCH
        SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
    END CATCH
END
