obtenerSiguienteClave.sql:
BEGIN
SET NOCOUNT ON;
DECLARE @siguiente AS INT;
SELECT ISNULL(MAX(cve_sucursal),0) + 1 clave FROM SG_sucursales;
END

Nuevo.sql:
BEGIN
	SET NOCOUNT ON;	
	BEGIN TRY
		INSERT INTO SG_Sucursales(
			cve_sucursal,
			cve_empresa,
			nombre,
			calle,
			colonia,
			delgamunic,
			estado,
			cp,
			entrecalles,
			activo,
			id_responsable,
			numreferencia,
			cve_tipo,
			cve_region,
			cve_zona
		)
		VALUES(
			@cve_sucursal,
			@cve_empresa,
			@nombre,
			@calle,
			@colonia,
			@delgamunic,
			@estado,
			@cp,
			@entrecalles,
			@activo,
			@id_responsable,
			@numreferencia,
			@cve_tipo,
			@cve_region,
			@cve_zona
		)
		SELECT 1  estatus, 'Registro guardado correctamente' mensaje;
	END TRY BEGIN CATCH	
		SELECT -1 AS estatus,  'Error: ' + ERROR_MESSAGE() mensaje;
	END CATCH	
END
